mapper.select:
  	select 
    
    from bill_info t, bill_fee t1
    where t.delete_flag != 1 and t.id = t1.bill_info_id
    
		and t.customer_name like concat(concat('%', #{customerName}), '%')
	
	
		and t.is_finsh_all = #{isFinshAll}
	
	
		and t.is_finsh_fee = #{isFinshFee}
	
	
		and DATE_FORMAT(t.create_date, '%Y-%m-%d')  >=  DATE_FORMAT(#{beginDate}, '%Y-%m-%d')
	
	
		and DATE_FORMAT(t.create_date, '%Y-%m-%d')  <=  DATE_FORMAT(#{endDate}, '%Y-%m-%d')
	
	order by t.create_date desc LIMIT #{beginNo}, #{pageSize}

select count(*) from bill_info t, bill_fee t1 where t.delete_flag != 1 and t.id = t1.bill_info_id
and t.customer_name like concat(concat('%', #{customerName}), '%')
and t.is_finsh_all = #{isFinshAll}
and t.is_finsh_fee = #{isFinshFee}
and DATE_FORMAT(t.create_date, '%Y-%m-%d') >= DATE_FORMAT(#{beginDate}, '%Y-%m-%d')
and DATE_FORMAT(t.create_date, '%Y-%m-%d') <= DATE_FORMAT(#{endDate}, '%Y-%m-%d')
